mapper.select:
SELECT

FROM dc_job_transHdfs a

WHERE a.ID = #{id}

	SELECT

	  FROM dc_job_transHdfs a

	 WHERE a.DEL_FLAG = #{DEL_FLAG_NORMAL} 


            AND a.id=#{id}


			 AND INSTR(a.job_name, #{jobName})>0  



	${sqlMap.dsf}
 ORDER BY a.UPDATE_DATE DESC

mapper.update:
UPDATE dc_job_transHdfs
JOB_NAME = #{jobName}, JOB_DESC = #{jobDesc}, SRC_HDFS_ADDRESS = #{srcHdfsAddress}, SRC_HDFS_DIR = #{srcHdfsDir}, SRC_HDFS_VERSION = #{srcHdfsVersion}, OUTPUT_DIR = #{outPutDir}, LOG_DIR = #{logDir}, COPY_NUM = #{copyNum}, IS_OVERRIDE = #{isOverride}, STATUS = #{status}, REMARKS = #{remarks}, SORT_NUM = #{sortNum}, remarks = #{remarks}, update_by = #{updateBy.id}, update_date = #{updateDate}, sqr = #{sqr}
WHERE ID = #{id}

UPDATE dc_job_transHdfs SET 
	DEL_FLAG = #{DEL_FLAG_DELETE}
WHERE ID = #{id}
